import React from 'react';
import * as S from './PictureCircle.style';

const PICTURESIZE = {
	xsmall: '3.5rem',
	small: '5rem',
	midium: '7.8rem',
	large: '10rem',
};

const PictureCircle = props => {
	return (
		<S.PictureCircle
			size={PICTURESIZE[props.size]}
			src={
				props.src
					? props.src
					: 'data:image/jpeg;base64,'
			}
			css={props.css}
		/>
	);
};

export default PictureCircle;
